<span class="token keyword">class</span> <span class="token class-name-definition class-name">Etudiant</span> <span class="token keyword">extends</span> <span class="token class-name">Personne</span> <span class="token punctuation">{</span>
    <span class="token keyword">private</span> ine<span class="token punctuation">;</span>

    <span class="token keyword">function</span> <span class="token function-definition function">__construct</span><span class="token punctuation">(</span><span class="token variable">$ine</span><span class="token punctuation">,</span> <span class="token variable">$nom</span><span class="token punctuation">,</span> <span class="token variable">$age</span><span class="token punctuation">)</span><span class="token punctuation">{</span>
        <span class="token keyword static-context">parent</span><span class="token operator">::</span><span class="token function">__construct</span><span class="token punctuation">(</span><span class="token variable">$nom</span><span class="token punctuation">,</span> <span class="token variable">$age</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
