    <span class="token punctuation">}</span>
<span class="token punctuation">}</span>

<span class="token variable">$etudiant</span> <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">Etudiant</span><span class="token punctuation">(</span><span class="token string double-quoted-string">"0X…"</span><span class="token punctuation">,</span> <span class="token string double-quoted-string">"Valentin"</span><span class="token punctuation">,</span> <span class="token number">34</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
